import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import M from 'materialize-css';

const SignUp = () => {
    const navigate = useNavigate();
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [image, setImage] = useState(null);
    const [url, setUrl] = useState(undefined);

    // Function to handle form submission
    const uploadFields = useCallback(() => {
        if (!image) {
            postData();
        } else {
            const data = new FormData();
            data.append('file', image);
            data.append('upload_preset', 'instagram-clone'); // Replace with your upload preset
            data.append('cloud_name', 'your-cloud-name'); // Replace with your cloud name

            fetch('https://api.cloudinary.com/v1_1/your-cloud-name/image/upload', {
                method: 'POST',
                body: data,
            })
                .then((res) => res.json())
                .then((data) => {
                    setUrl(data.url);
                    postData(data.url);
                })
                .catch((err) => console.log(err));
        }
    }, [image]);

    const postData = (profilePicUrl) => {
        const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;

        if (!emailRegex.test(email)) {
            M.toast({ html: 'Invalid email', classes: '#c62828 red darken-3' });
            return;
        }

        fetch('/signup', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                name,
                email,
                password,
                pic: profilePicUrl || '',
            }),
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.error) {
                    M.toast({ html: data.error, classes: '#c62828 red darken-3' });
                } else {
                    M.toast({
                        html: 'Signed up successfully',
                        classes: '#43a047 green darken-1',
                    });
                    navigate('/signin'); // Redirect to SignIn page after successful signup
                }
            })
            .catch((err) => console.log(err));
    };

    useEffect(() => {
        if (url) {
            postData(url);
        }
    }, [url]);

    return (
        <div className="mycard">
            <div className="card auth-card input-field">
                <h2>Instagram</h2>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
                <input
                    type="text"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <div className="file-field input-field">
                    <div className="btn">
                        <span>Upload Profile Picture</span>
                        <input
                            type="file"
                            onChange={(e) => setImage(e.target.files[0])}
                        />
                    </div>
                    <div className="file-path-wrapper">
                        <input
                            className="file-path validate"
                            type="text"
                            placeholder="Choose a profile picture"
                        />
                    </div>
                </div>
                <button
                    className="btn waves-effect waves-light #64b5f6 blue darken-1"
                    onClick={uploadFields}
                >
                    SignUp
                </button>
                <h5>
                    <Link to="/signin">Already have an account? Login</Link>
                </h5>
            </div>
        </div>
    );
};

export default SignUp;
