import React, { useState, useEffect, useCallback } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import M from 'materialize-css';

const SignUp = () => {
    const navigate = useNavigate();
    const [name, setName] = useState('');
    const [password, setPassword] = useState('');
    const [email, setEmail] = useState('');
    const [image, setImage] = useState(null);
    const [url, setUrl] = useState(undefined);

    // Function to handle form submission
    const uploadFields = useCallback(() => {
        if (!/^[^\s@]+@[^\s@]+\.[^\s@]+$/.test(email)) {
            M.toast({ html: 'Invalid email', classes: '#c62828 red darken-3' });
            return;
        }

        fetch('/signup', {
            method: 'POST',
            headers: { 'Content-Type': 'application/json' },
            body: JSON.stringify({
                name,
                password,
                email,
                pic: url,
            }),
        })
            .then((res) => res.json())
            .then((data) => {
                if (data.error) {
                    M.toast({ html: data.error, classes: '#c62828 red darken-3' });
                } else {
                    M.toast({ html: data.message, classes: '#43a047 green darken-1' });
                    navigate('/signin');
                }
            })
            .catch((err) => console.error('Error during signup:', err));
    }, [name, password, email, url, navigate]);

    // Trigger uploadFields when URL is updated
    useEffect(() => {
        if (url) {
            console.log('URL updated:', url); // Debug
            uploadFields();
        }
    }, [url, uploadFields]);

    // Function to handle image upload
    const uploadPic = () => {
        const data = new FormData();
        data.append('file', image);
        data.append('upload_preset', 'new-insta');
        data.append('cloud_name', 'cnq');

        fetch('https://api.cloudinary.com/v1_1/dqkmnrjdr/image/upload', {
            method: 'POST',
            body: data,
        })
            .then((res) => res.json())
            .then((data) => {
                console.log('Image uploaded:', data.url); // Debug
                setUrl(data.url);
            })
            .catch((err) => console.error('Image upload error:', err));
    };

    // Function to initiate signup process
    const PostData = () => {
        console.log('Signup initiated'); // Debug
        if (image) {
            uploadPic();
        } else {
            uploadFields();
        }
    };

    return (
        <div className="mycard">
            <div className="card auth-card input-field">
                <h2>Instagram</h2>
                <input
                    type="text"
                    placeholder="Name"
                    value={name}
                    onChange={(e) => setName(e.target.value)}
                />
                <input
                    type="email"
                    placeholder="Email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                />
                <input
                    type="password"
                    placeholder="Password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                />
                <div className="file-field input-field">
                    <div className="btn #64b5f6 blue darken-1">
                        <span>Upload pic</span>
                        <input type="file" onChange={(e) => setImage(e.target.files[0])} />
                    </div>
                    <div className="file-path-wrapper">
                        <input className="file-path validate" type="text" />
                    </div>
                </div>
                <button
                    className="btn waves-effect waves-light #64b5f6 blue darken-1"
                    onClick={PostData}
                >
                    SignUp
                </button>
                <h5>
                    <Link to="/signin">Already have an account?</Link>
                </h5>
            </div>
        </div>
    );
};

export default SignUp;
