import React, { useContext } from "react";
import { Link } from "react-router-dom";
import { UserContext } from "../App";

const NavBar = () => {
  const { state, dispatch } = useContext(UserContext);

  const renderList = () => {
    // Check if user is logged in by verifying if `state.user` exists
    if (state && state.user) {
      return [
        <li key="1"><Link to="/profile">Profile</Link></li>,
        <li key="2"><Link to="/createpost">Create Post</Link></li>,
        <li key="3"><Link to="/signin" onClick={() => { 
          // Log out logic (clear user from localStorage and update state)
          localStorage.removeItem('user');
          dispatch({ type: "USER", payload: null });
        }}>Logout</Link></li>
      ];
    } else {
      return [
        <li key="1"><Link to="/signin">Signin</Link></li>,
        <li key="2"><Link to="/signup">Signup</Link></li>
      ];
    }
  };

  return (
    <nav>
      <div className="nav-wrapper white">
        <Link to="/" className="brand-logo left">Instagram</Link>
        <ul id="nav-mobile" className="right">
          {renderList()}
        </ul>
      </div>
    </nav>
  );
};

export default NavBar;
