import React from "react";

const Profile = () => {
    return (
        <div style={{
            maxWidth: "550px",
            margin: "0px auto",
            padding: "8px",
        }}>
            <div style={{
                display: "flex",
                flexWrap: "wrap", 
                justifyContent: "space-around",
                margin: "0px 0px", // Increased spacing around the profile section
                borderBottom: "1px solid grey",
                paddingBottom: "20px", // Added space below the section
            }}>
                <div style={{
                    marginBottom: "20px", // Space below the image
                    flexBasis: "140px", // Ensures the image box scales properly
                }}>
                    <img
                        style={{
                            width: "140px",
                            height: "140px",
                            borderRadius: "80px",
                            marginBottom: "20px", // Added space below the image
                        }}
                        src="https://img.freepik.com/free-photo/pretty-zo-qn-zith-photo-frq-e-close-up_1187-5658.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                        alt="Profile"
                    />
                </div>
                <div style={{
                    flex: 1, // Takes remaining space
                    minWidth: "200px", // Prevents shrinking on smaller devices
                    marginTop: "20px", // Added space above the name and stats
                }}>
                    <h4 style={{
                        margin: "0px",
                        marginBottom: "10px", // Space below the name
                    }}>hinata hyuga</h4>
                    <div style={{
                        display: "flex",
                        justifyContent: "space-between",
                        width: "100%",
                        maxWidth: "300px",
                        marginTop: "10px", // Space above the stats
                    }}>
                        <h6>40 posts</h6>
                        <h6>40 followers</h6>
                        <h6>40 following</h6>
                    </div>
                </div>
            </div>

            <div
                className="gallery"
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fit, minmax(100px, 1fr))",
                    gap: "10px",
                    marginTop: "20px",
                }}
            >
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
            </div>
        </div>
    );
};

export default Profile;
