import React from "react";

const Profile = () => {
    return (
        <div style={{
            maxWidth: "850px", // Increased width for large screens
            margin: "0px auto",
            padding: "20px", // Increased padding for larger screens
        }}>
            <div style={{
                display: "flex",
                flexWrap: "wrap",
                justifyContent: "space-around",
                alignItems: "center", // Align items vertically
                margin: "20px 0px",
                borderBottom: "1px solid grey",
                paddingBottom: "30px", // Increased padding for better spacing
            }}>
                <div style={{
                    marginBottom: "20px",
                    flexBasis: "150px", // Slightly larger image container for large screens
                }}>
                    <img
                        style={{
                            width: "150px",
                            height: "150px",
                            borderRadius: "75px", // Adjusted for the new size
                            marginBottom: "20px",
                        }}
                        src="https://img.freepik.com/free-photo/pretty-zo-qn-zith-photo-frq-e-close-up_1187-5658.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                        alt="Profile"
                    />
                </div>
                <div style={{
                    flex: 1,
                    minWidth: "300px", // Increased minimum width
                    marginTop: "20px",
                }}>
                    <h4 style={{
                        margin: "0px",
                        marginBottom: "15px", // More space below the name
                        fontSize: "1.5rem", // Larger font size for large screens
                    }}>hinata hyuga</h4>
                    <div style={{
                        display: "flex",
                        justifyContent: "space-between",
                        width: "100%",
                        maxWidth: "400px", // Increased max width for stats section
                        marginTop: "15px",
                    }}>
                        <h6>40 posts</h6>
                        <h6>40 followers</h6>
                        <h6>40 following</h6>
                    </div>
                </div>
            </div>

            <div
                className="gallery"
                style={{
                    display: "grid",
                    gridTemplateColumns: "repeat(auto-fit, minmax(150px, 1fr))", // Larger grid items
                    gap: "15px", // Increased spacing between images
                    marginTop: "30px",
                }}
            >
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
                <img
                    className="item"
                    src="https://img.freepik.com/free-photo/pretty-girl-showing-gesture-like-frame_1187-5744.jpg?uid=R147514784&ga=GA1.1.1158692380.1730345942&semt=ais_hybrid"
                    alt="Gallery"
                    style={{ width: "100%", borderRadius: "10px" }}
                />
            </div>
        </div>
    );
};

export default Profile;
